mapper.update:
update
    grapeems_announce
set
    announceTitle = #{announceTitle}, announceContent = #{announceContent}
where
    announceId = #{announceId}

update
    grapeems_announce
set
    isDeleted = #{isDeleted}
where
    announceId = #{announceId}

mapper.select:
select announceId, announceCreateTime, announceTitle, announceContent, isDeleted, deptId from grapeems_announce where isDeleted = 1
and deptId = #{deptId}
and announceCreateTime between #{startTime} and #{endTime}
